import React from 'react';

const MainSection = ({ orders, totalDelivered, cancelOrder }) => {
  const getTableRow = (order, index, array) => {
    // Generate random time for demonstration purposes
    const randomMinutes = Math.floor(Math.random() * 60);
    const randomSeconds = Math.floor(Math.random() * 60);
    const randomTime = `${randomMinutes} min ${randomSeconds} sec`;

    const isLastRow = index === array.length - 1;

    return (
      <tr key={order.id}>
        <td className={isLastRow ? 'bold' : ''}>{order.id}</td>
        <td>{order.stage}</td>
        <td>{randomTime}</td>
        <td>
          {order.stage !== 'Order Ready' && order.stage !== 'Order Placed' && (
            <button onClick={() => cancelOrder(order.id)}>Cancel</button>
          )}
        </td>
        {isLastRow && totalDelivered > 0 && (
          <>
            <td colSpan="2"></td>
            <td colSpan="1">
              <div className="total-delivered">
                <h3>Total Delivered Today</h3>
                <p>{String(totalDelivered).padStart(3, '0')}</p>
              </div>
            </td>
          </>
        )}
      </tr>
    );
  };

  const sortedOrders = orders.slice().sort((a, b) => {
    const timeA = a.timeInSeconds || 0;
    const timeB = b.timeInSeconds || 0;
    return timeB - timeA;
  });

  return (
    <div className="main-container">
      <h2>Main Section</h2>
      <table className="bordered">
        <thead>
          <tr>
            <th>Order Id</th>
            <th>Stage</th>
            <th>Total time spent (time from order placed)</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {sortedOrders.map((order, index, array) => getTableRow(order, index, array))}
        </tbody>
      </table>
    </div>
  );
};

export default MainSection;
