import { PLACE_ORDER, NEXT_STAGE, PICK_ORDER, CANCEL_ORDER } from './actions';

const initialState = {
  orders: [],
  totalDelivered: 0,
};

const getNextStage = (currentStage) => {
  switch (currentStage) {
    case 'Order Placed':
      return 'Order in Making';
    case 'Order in Making':
      return 'Order Ready';
    case 'Order Ready':
      return 'Order Picked';
    default:
      return currentStage;
  }
};

const pizzaReducer = (state = initialState, action) => {
  switch (action.type) {
    case PLACE_ORDER:
      return {
        ...state,
        orders: [...state.orders, action.payload],
      };
    case NEXT_STAGE:
      return {
        ...state,
        orders: state.orders.map((order) =>
          order.id === action.payload
            ? {
                ...order,
                stage: getNextStage(order.stage),
                time: order.stage === 'Order Placed' ? '0 min 0 sec' : order.time,
              }
            : order
        ),
      };
    case PICK_ORDER:
      return {
        ...state,
        orders: state.orders.map((order) =>
          order.id === action.payload ? { ...order, stage: 'Order Picked' } : order
        ),
      };
    case CANCEL_ORDER:
      return {
        ...state,
        orders: state.orders.filter((order) => order.id !== action.payload),
      };
    default:
      return state;
  }
};

export default pizzaReducer;
