import React from 'react';
import { connect } from 'react-redux';
import { placeOrder, nextStage, pickOrder, cancelOrder } from './actions';
import PizzaOrderForm from './PizzaOrderForm';
import PizzaStagesSection from './PizzaStagesSection';
import MainSection from './MainSection';
import './App.css'; // Import your styles

const App = ({ orders, totalDelivered, placeOrder, nextStage, pickOrder, cancelOrder }) => {
  return (
    <div className="app-container">
      <h1>Pizza Shop</h1>

      {/* Pizza Order Form */}
      <PizzaOrderForm placeOrder={placeOrder} />

      {/* Pizza Stages Section */}
      <PizzaStagesSection orders={orders} nextStage={nextStage} pickOrder={pickOrder} cancelOrder={cancelOrder} />

      {/* Main Section */}
      <MainSection orders={orders} totalDelivered={totalDelivered} />
    </div>
  );
};

const mapStateToProps = (state) => ({
  orders: state.orders,
  totalDelivered: state.totalDelivered,
});

const mapDispatchToProps = {
  placeOrder,
  nextStage,
  pickOrder,
  cancelOrder,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
