import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { cancelOrder, placeOrder } from './actions';
import TotalDeliveredForm from './TotalDeliveredForm';

const MainSection = ({ orders }) => {
  const [pickedOrders, setPickedOrders] = useState([]);
  const [newOrder, setNewOrder] = useState({ type: 'Veg', size: 'Large', base: 'Thin' });
  const dispatch = useDispatch();

  useEffect(() => {
    const newPickedOrders = orders.filter((order) => order.stage === 'Order Picked');
    setPickedOrders(newPickedOrders);
  }, [orders]);

  const handleCancelOrder = (orderId) => {
    dispatch(cancelOrder(orderId));
  };

  const handlePlaceOrder = () => {
    if (orders.length >= 10) {
      alert('Not taking any order for now. Order limit reached.');
    } else {
      // Place the new order
      dispatch(placeOrder(newOrder));

      // Reset the new order form
      setNewOrder({ type: 'Veg', size: 'Large', base: 'Thin' });
    }
  };

  const getTableRow = (order, index, array) => {
    const makingTime = order.size === 'Small' ? 3 : order.size === 'Medium' ? 4 : 5;

    const currentTimeInSeconds = new Date().getTime() / 1000;
    const elapsedSeconds = currentTimeInSeconds - order.startTime;

    const isDelayed = order.stage !== 'Order Placed' && elapsedSeconds > 180;

    const randomMinutes = Math.floor(Math.random() * 60);
    const randomSeconds = Math.floor(Math.random() * 60);
    const randomTime = `${randomMinutes} min ${randomSeconds} sec`;

    return (
      <tr key={order.id} className={isDelayed ? 'delayed' : ''}>
        <td>{order.id}</td>
        <td>{order.stage}</td>
        <td>{order.stage === 'Order Placed' ? '0 min 0 sec' : randomTime}</td>
        <td>
          {order.stage !== 'Order Ready' && order.stage !== 'Order Placed' && order.stage !== 'Order Picked' && (
            <button onClick={() => handleCancelOrder(order.id)}>Cancel</button>
          )}
        </td>
      </tr>
    );
  };

  const sortedOrders = orders.slice().sort((a, b) => {
    const timeA = a.timeInSeconds || 0;
    const timeB = b.timeInSeconds || 0;
    return timeB - timeA;
  });

  return (
    <div className="main-container">
      <h2>Main Section</h2>
      <table className="bordered">
        <thead>
          <tr>
            <th>Order Id</th>
            <th>Stage</th>
            <th>Total time spent (time from order placed)</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{sortedOrders.map((order, index, array) => getTableRow(order, index, array))}</tbody>
      </table>
      <TotalDeliveredForm totalPicked={pickedOrders.length} />

      {/* New Order Form */}
      <div className="form-container">
        <h2>Place Pizza Order</h2>
        <form>
          <label>
            Type:
            <select name="type" value={newOrder.type} onChange={(e) => setNewOrder({ ...newOrder, type: e.target.value })}>
              <option value="Veg">Veg</option>
              <option value="Non-Veg">Non-Veg</option>
            </select>
          </label>
          <label>
            Size:
            <select name="size" value={newOrder.size} onChange={(e) => setNewOrder({ ...newOrder, size: e.target.value })}>
              <option value="Large">Large</option>
              <option value="Medium">Medium</option>
              <option value="Small">Small</option>
            </select>
          </label>
          <label>
            Base:
            <select name="base" value={newOrder.base} onChange={(e) => setNewOrder({ ...newOrder, base: e.target.value })}>
              <option value="Thin">Thin</option>
              <option value="Thick">Thick</option>
            </select>
          </label>
          <button type="button" onClick={handlePlaceOrder}>
            Place Order
          </button>
        </form>
      </div>
    </div>
  );
};

export default MainSection;
