// src/actions/index.js

export const placeOrder = (order) => {
  return {
    type: 'PLACE_ORDER',
    payload: order
  };
};

export const updateOrderStage = (orderId, newStage) => {
  return {
    type: 'UPDATE_ORDER_STAGE',
    payload: { orderId, newStage }
  };
};

export const cancelOrder = (orderId) => {
  return {
    type: 'CANCEL_ORDER',
    payload: { orderId }
  };
};
