import React from 'react';

const PizzaStagesSection = ({ orders, nextStage, pickOrder }) => {
  const getOrderCard = (order) => (
    <div key={order.id} className={`order-card ${order.stage === 'Order Placed' ? 'placed' : ''}`}>
      <p>Order ID: {order.id}</p>
      <p>Time: {order.time}</p>
      <div className="actions">
        {order.stage !== 'Order Picked' && <button onClick={() => nextStage(order.id)}>Next</button>}
      </div>
    </div>
  );

  return (
    <div className="stages-container">
      <h2>Pizza Stages</h2>
      <div className="order-placed">
        <h3>Order Placed</h3>
        {orders.filter((order) => order.stage === 'Order Placed').map(getOrderCard)}
      </div>
      <div className="order-in-making">
        <h3>Order in Making</h3>
        {orders.filter((order) => order.stage === 'Order in Making').map(getOrderCard)}
      </div>
      <div className="order-ready">
        <h3>Order Ready</h3>
        {orders.filter((order) => order.stage === 'Order Ready').map(getOrderCard)}
      </div>
      <div className="order-picked">
        <h3>Order Picked</h3>
        {orders.filter((order) => order.stage === 'Order Picked').map((order) => (
          <div key={order.id} className="order-card picked">
            <p>Order ID: {order.id}</p>
            <p>Picked</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PizzaStagesSection;
