// src/reducers/index.js

const initialState = {
  orders: []
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'PLACE_ORDER':
      const newOrder = {
        id: state.orders.length + 1,
        type: action.payload.type,
        size: action.payload.size,
        base: action.payload.base,
        stage: 'Order Placed',
        timeSpent: '0 min 0 sec',
        action: 'Cancel Button',
        createdAt: Date.now()
      };
      return {
        ...state,
        orders: [...state.orders, newOrder]
      };

    case 'UPDATE_ORDER_STAGE':
      const { orderId, newStage } = action.payload;
      const updatedOrders = state.orders.map((order) =>
        order.id === orderId
          ? {
              ...order,
              stage: newStage,
              action: newStage === 'Order Ready' ? 'Picked Button' : 'Cancel Button'
            }
          : order
      );
      return {
        ...state,
        orders: updatedOrders
      };

    case 'CANCEL_ORDER':
      const { orderId: cancelledOrderId } = action.payload;
      const remainingOrders = state.orders.filter((order) => order.id !== cancelledOrderId);
      return {
        ...state,
        orders: remainingOrders
      };

    default:
      return state;
  }
};

export default rootReducer;
