// OrderForm.jsx

import React, { useState, useEffect } from 'react';

const OrderForm = ({ placeOrder }) => {
  const [order, setOrder] = useState({
    type: 'Veg',
    size: 'Large',
    base: 'Thin',
    timer: 0, // New state for the timer
  });

  useEffect(() => {
    // Set the initial time based on the selected pizza size
    const makingTime = getMakingTime(order.size);
    setOrder((prevOrder) => ({ ...prevOrder, timer: makingTime * 60 }));
  }, [order.size]);

  const handleChange = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    placeOrder(order);
    setOrder({
      type: 'Veg',
      size: 'Large',
      base: 'Thin',
      timer: 0, // Reset the timer on order placement
    });
  };

  const getMakingTime = (size) => {
    switch (size) {
      case 'Small':
        return 3;
      case 'Medium':
        return 4;
      case 'Large':
        return 5;
      default:
        return 0;
    }
  };

  return (
    <div className="form-container">
      <h2>Place Pizza Order</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Type:
          <select name="type" value={order.type} onChange={handleChange}>
            <option value="Veg">Veg</option>
            <option value="Non-Veg">Non-Veg</option>
          </select>
        </label>
        <label>
          Size:
          <select name="size" value={order.size} onChange={handleChange}>
            <option value="Large">Large</option>
            <option value="Medium">Medium</option>
            <option value="Small">Small</option>
          </select>
        </label>
        <label>
          Base:
          <select name="base" value={order.base} onChange={handleChange}>
            <option value="Thin">Thin</option>
            <option value="Thick">Thick</option>
          </select>
        </label>
        <button type="submit">Place Order</button>
      </form>
      {order.timer > 0 && (
        <p>Time remaining: {Math.floor(order.timer / 60)} min {order.timer % 60} sec</p>
      )}
    </div>
  );
};

export default OrderForm;
