import React from 'react';

const MainSection = ({ orders, totalDelivered }) => {
  return (
    <div className="main-container">
      <h2>Main Section</h2>
      <div className="main-section">
        <div className="in-progress">
          <h3>In Progress</h3>
          {orders.map((order) => (
            <div key={order.id} className={`order-card ${order.stage === 'Order Ready' ? 'ready' : ''}`}>
              <p>Order ID: {order.id}</p>
              <p>Remaining Time: {order.remainingTime}</p>
            </div>
          ))}
        </div>
        <div className="total-delivered">
          <h3>Total Delivered Today</h3>
          <p>{totalDelivered}</p>
        </div>
      </div>
    </div>
  );
};

export default MainSection;
