import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateOrderStage, cancelOrder } from '../actions';

const PizzaList = () => {
  const orders = useSelector((state) => state.orders);
  const dispatch = useDispatch();

  const handleStageChange = (orderId, newStage) => {
    dispatch(updateOrderStage(orderId, newStage));
  };

  const handleCancelOrder = (orderId) => {
    dispatch(cancelOrder(orderId));
  };

  const calculateTimeElapsed = (createdAt) => {
    const currentTime = Date.now();
    const elapsedMilliseconds = currentTime - createdAt;
    const minutes = Math.floor(elapsedMilliseconds / (1000 * 60));
    const seconds = Math.floor((elapsedMilliseconds % (1000 * 60)) / 1000);
    return `${minutes} min ${seconds} sec`;
  };

  return (
    <div>
      <h2>Pizza List</h2>
      <ul>
        {orders.map((order) => (
          <li key={order.id} style={{ color: order.timeSpent > 180000 ? 'red' : 'black' }}>
            Order Id: {order.id} - {order.stage} - {order.timeSpent} - {order.action}
            <button onClick={() => handleStageChange(order.id, 'Order In Making')}>Next</button>
            <button onClick={() => handleStageChange(order.id, 'Order Ready')}>Picked</button>
            <button onClick={() => handleCancelOrder(order.id)}>Cancel</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PizzaList;
