import React from 'react';

const MainSection = ({ orders, totalDelivered }) => {
  const getTableColumn = (order) => (
    <td key={order.id}>
      <p>Order ID: {order.id}</p>
      <p>Stage: {order.stage}</p>
      <p>Total time spent: {order.totalTime}</p>
    </td>
  );

  return (
    <div className="main-container">
      <h2>Main Section</h2>
      <table>
        <thead>
          <tr>
            <th>Order Placed</th>
            <th>Order in Making</th>
            <th>Order Ready</th>
            <th>Order Picked</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            {orders.map(getTableColumn)}
          </tr>
        </tbody>
      </table>
      <div className="total-delivered">
        <h3>Total Delivered Today</h3>
        <p>{totalDelivered}</p>
      </div>
    </div>
  );
};

export default MainSection;
